    public class CacheMonitor extends Thread {

        ...

        /** Clears the temporary files associated with the session. Call on logout */
        public void clean_session_cache(String session_id) {
            Pattern file_pattern = Pattern.compile("^sess" + session_id + "-.*$");

            File[] file_list = this.cache_dir.listFiles();
            if (file_list == null)
                return;
            for (File tmp_file : file_list) {
                String file_name = tmp_file.getName();

                Matcher match = file_pattern.matcher(file_name);
                if (match.find())
                    tmp_file.delete();
            }

        }

