    public class CacheMonitor extends Thread {

        // In Minutes -- Default Temporary file expiration
        public static final long DEFAULT_TMP_EXPIRY = 30;

        public static final long MONITORING_FREQUENCY = 10 * 1000; // Every 10 seconds
        private File cache_dir;

        public CacheMonitor(File cache_dir) {
            super();
            this.cache_dir = cache_dir;
        }

        /** Get a temporary file. This way the name is generated properly */
        public File getTempFile(String name, String session_id) throws IOException {
            return getTempFile(name, session_id, DEFAULT_TMP_EXPIRY);
        }

        /** Get a temporary file. This way the name is generated properly */
        public File getTempFile(String name, String session_id, long expiry_minutes) 
        throws IOException {
            long time_stamp = (long)(System.currentTimeMillis()/1000) + expiry_minutes*60;
            return File.createTempFile("sess" + session_id + "-" + time_stamp + "-" + name, 
                ".tmp", this.cache_dir);
        }

        ...

        /** The monitoring thread */
        @Override
        public void run() {
            Pattern file_pattern = Pattern.compile("^[a-zA-Z0-9]+-([0-9]+)-.*$");

            while (true) {
                try {
                    // Generate the list of temporary files
                    File[] file_list = this.cache_dir.listFiles();
                    if (file_list == null)
                        continue;
                    for (File tmp_file : file_list) {
                        String file_name = tmp_file.getName();

                        Matcher match = file_pattern.matcher(file_name);
                        Boolean to_delete = false;
                        if (!match.find())
                            // If we don't know the file name, then it will be erased
                            to_delete = true;
                        else {
                            Long exp_time = Long.parseLong(match.group(1));
                            Long current_time = System.currentTimeMillis() / 1000;
                            if (exp_time < current_time)
                                // To be ereased since it is expired
                                to_delete = true;
                        }
                        if (to_delete)
                            tmp_file.delete();
                    }

                    sleep(MONITORING_FREQUENCY);
                } catch (InterruptedException e) {
                    // Restore the interrupted status
                    Thread.currentThread().interrupt();
                }
            }
        }

